import React from 'react';

const KEY = '08068492c90f845955929246b3b293b1';
const API = `http://ws.audioscrobbler.com/2.0/?method=track.search&track=joji&api_key=${KEY}&format=json`;

class Search extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isActive: false,
            results: [],
            query: '',
            message: '',
            loading: false,
        }
    }

    componentDidMount() {
        fetch(API)
          .then(response => response.json())
          .then(data => this.setState({ results: data }));
      }

    handleActive = () => {
        if (this.state.isActive === false) {
            this.setState({ isActive: true });
        } else {
            this.setState({ isActive: false });
        }
    }

    handleSearch = (event) => {
        const query = event.target.value;
            this.setState({ query, loading: true, message: ''  } );
    }

    render() {
        console.log(this.state.results.results);
        return (
            <div className={this.state.isActive === true ? "search active" : "search"}>
                <input 
                type="text" value=""
                id="search-input"
                placeholder="Search..."
                onChange={this.handleSearch}
                />
                <button onClick={this.state.isActive === false ? this.handleActive : this.handleSearch} className="search-btn">
                    {this.state.isActive === true ? "Go" : "Search"}
                </button>
            </div>
        )
    }
}

export default Search;
