import React from 'react';
import axios from 'axios';

class Search extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isActive: false,
            results: [],
            searchValue: '',
        }
    }

    apiCall = query => {
        const KEY = '08068492c90f845955929246b3b293b1';
        const API = `http://ws.audioscrobbler.com/2.0/?method=track.search&track=${query}&api_key=${KEY}&format=json`;
        fetch(API)
          .then(response => response.json())
          .then(data => this.setState({ results: data }));
      }

    handleActive = () => {
        if (this.state.isActive === false) {
            this.setState({ isActive: true });
        } else {
            this.setState({ isActive: false });
        }
    }

    handleChange = event => {
        const searchValue = event.target.value;
        this.setState({ searchValue: searchValue });
    }

    handleSearch = () => {
        this.apiCall(this.state.searchValue);
        console.log(this.state.results.results);
    }

    render() {
        return (
            <div className={this.state.isActive === true ? "search active" : "search"}>
                <input value={this.state.searchValue} onChange={event => this.handleChange(event)} type="text"/>
                <button onClick={this.state.isActive === false ? this.handleActive : this.handleSearch} className="search-btn">
                    {this.state.isActive === true ? "Go" : "Search"}
                </button>
                 <div className="output">
                   {/* {this.state.results.map(song => {
                       return <div className="song-item">
                                <h2>{song.artist}</h2>
                                <h3>{song.name}</h3>
                                <p>{song.listeners}</p>
                             </div>
                   })}  */}
                </div>
            </div>
        )
    }
}

export default Search;
